import pandas as pdimport numpy as np# print(dataset.info())# 资料载入# dataset = pd.read_csv('http://archive.ics.uci.edu/ml/machine-learning-databases/iris/iris.data', header=None)# print(dataset.shape)  # (150, 5)# print(dataset.head(5))# print(dataset.describe())# print(dataset.info())N = 20df = pd.DataFrame({    'A': pd.date_range(start='2017-01-01', periods=N, freq='D'),    'x': np.linspace(0, stop=N-1, num=N),    'y': np.random.rand(N),    'C': np.random.choice(['Low', 'Medium', 'High'], N).tolist(),    'D': np.random.normal(100, 10, size=N).tolist()})# print(df)df_reindexed = df.reindex(index=[0, 1, 2, 3, 4, 5], columns=['A', 'C', 'B', 'D', 'x', 'y'])print(df_reindexed)print('--------------------------------------')df2 = df.reindex(index=[0, 1, 3, 5, 7], columns=['A', 'B', 'C', 'D', 'x', 'y'])print(df2)